import p5 from 'p5'

function getRandomArbitrary(min, max) {
  return Math.random() * (max - min) + min
}

document.addEventListener('DOMContentLoaded', () => {
  const container = document.getElementsByClassName('prototype_5')[0]
  const frame = document.createElement('div')
  frame.classList.add('frame')
  frame.id = 'frame'
  container.appendChild(frame)

  let sketch = (p) => {
    var angle,
      gen = 333

    p.setup = () => {
      let canvas = p.createCanvas(800, 800)
      canvas.parent('frame')
      p.background(0)
      p.stroke(200, 0, 255, 80)
      p.fill(233, 0, 55, 30)
    }

    p.draw = () => {
      angle = sin(gen * 33) * 33

      p.push()
      p.translate(width / 2, height / 2)
      p.rotate(gen * 2)
      for (var i = 0; i < 133; i++) {
        p.rotate((3 * gen) / 33)
        p.curve(i, i, 0, angle + i, 133, angle - i, i + 133, i)
        //bezier(i, i, 0, angle + i, 333, angle + i, i + 333, i);
      }
      p.pop()

      p.push()
      p.translate(width / 2, height / 2)
      p.rotate(gen * 2)
      for (var i = 0; i < 133; i++) {
        p.rotate((3 * gen) / 33)
        p.stroke(255, 0, 242)
        p.strokeWeight(0.3)
        bezier(i, i, 0, cos(angle - i), 133, angle + i, i + 133, i)
      }
      p.pop()

      p.push()
      p.translate(width / 2, height / 2)
      p.rotate(gen * 2)
      for (var i = 0; i < 133; i++) {
        p.rotate((-3 * gen) / 33)
        p.stroke(255)
        p.strokeWeight(1)
        p.ellipse(i, 0, 0.3, 0.3)
      }
      p.pop()

      gen += 0.0009
    }
  }

  let myp5 = new p5(sketch)
})
