import p5 from 'p5'

function getRandomArbitrary(min, max) {
  return Math.random() * (max - min) + min
}

document.addEventListener('DOMContentLoaded', () => {
  const container = document.getElementsByClassName('prototype_5')[0]
  const frame = document.createElement('div')
  frame.classList.add('frame')
  frame.id = 'frame'
  container.appendChild(frame)

  let sketch = (p) => {
    let x = 100
    let y = 100

    p.setup = () => {
      let canvas = p.createCanvas(800, 800)
      canvas.parent('frame')
      p.background(0)
    }
    var comb = [
      [
        { x: 0.5, y: 0 },
        { x: 0, y: 0.5 },
        { x: 1, y: 0.5 },
        { x: 0.5, y: 1 },
        { x: 0.5, y: 0.5 }
      ],
      [
        { x: 0.5, y: 0 },
        { x: 0.5, y: 1 }
      ],
      [
        { x: 0.5, y: 0 },
        { x: 0.5, y: 1 },
        { x: 0.5, y: 0.5 }
      ],
      [
        { y: 0.5, x: 0 },
        { y: 0.5, x: 1 }
      ],
      [
        { y: 0.5, x: 0 },
        { y: 0.5, x: 1 },
        { y: 0.5, x: 0.5 }
      ],
      [
        { x: 0, y: 0 },
        { x: 1, y: 0 },
        { x: 0, y: 1 },
        { x: 1, y: 1 }
      ],
      [
        { x: 0, y: 0 },
        { x: 1, y: 0 },
        { x: 0, y: 1 },
        { x: 1, y: 1 },
        { x: 0.5, y: 0.5 }
      ],
      [
        { x: 0, y: 0 },
        { x: 0, y: 0.5 },
        { x: 0, y: 1 }
      ],
      [
        { x: 1, y: 0 },
        { x: 1, y: 0.5 },
        { x: 1, y: 1 }
      ],
      [
        { y: 0, x: 0 },
        { y: 0, x: 0.5 },
        { y: 0, x: 1 }
      ],
      [
        { y: 1, x: 0 },
        { y: 1, x: 0.5 },
        { y: 1, x: 1 }
      ],
      [
        { x: 0.75, y: 0 },
        { x: 0, y: 0.75 },
        { x: 1, y: 0.75 },
        { x: 0.75, y: 1 },
        { x: 0.75, y: 0.75 }
      ],
      [
        { x: 0.25, y: 0 },
        { x: 0, y: 0.25 },
        { x: 1, y: 0.25 },
        { x: 0.25, y: 1 },
        { x: 0.25, y: 0.25 }
      ],
      [
        { x: 0.75, y: 0 },
        { x: 0, y: 0.25 },
        { x: 1, y: 0.25 },
        { x: 0.75, y: 1 },
        { x: 0.75, y: 0.25 }
      ],
      [
        { x: 0.25, y: 0 },
        { x: 0, y: 0.75 },
        { x: 1, y: 0.75 },
        { x: 0.25, y: 1 },
        { x: 0.25, y: 0.75 }
      ]
    ]
    var pos = getRandomArbitrary(comb)
    var rnd = getRandomArbitrary()
    let colrnd = getRandomArbitrary()
    var t = 0,
      t0 = 1
    var step = getRandomArbitrary() * 15 + 4

    p.draw = () => {
      p.clear()
      p.noFill()

      if (colrnd > 0.6) p.stroke('#86837A')
      else if (colrnd > 0.7) p.stroke('#FFFF94')
      else p.stroke('#78756C')

      let fc = p.frameCount
      for (p of pos) {
        //i<100 if you use 100vh/w
        for (let i = 0; i < 50; i++) {
          let t1 = p.sin((i / 30) * rnd + t) * 1.2
          p.strokeWeight(t1 * 0.8)

          t += 0.0001
          if (fc % 100 == 0) {
            t0 += 0.1
            rnd = getRandomArbitrary()
            step = getRandomArbitrary() * 15 + 4
          }

          if (fc % 200 == 0) {
            colrnd = getRandomArbitrary()
            pos = getRandomArbitrary(comb)
          }

          p.ellipse(w * p.x, h * p.y, (100 - i) * step, i * step)
        }
      }
    }
  }

  let myp5 = new p5(sketch)
})
