import p5 from 'p5'

function getRandomArbitrary(min, max) {
  return Math.random() * (max - min) + min
}

document.addEventListener('DOMContentLoaded', () => {
  const container = document.getElementsByClassName('prototype_12')[0]
  const frame = document.createElement('div')
  frame.classList.add('frame')
  frame.id = 'frame'
  container.appendChild(frame)

  //sketch
  let sketch = (p) => {
    var angle,
      gen = 333

    p.setup = () => {
      let canvas = p.createCanvas(800, 800)
      canvas.parent('frame')
      p.stroke(200, 0, 255, 80)
      //p.fill(127, 2, 55, 60)
      p.fill(0, 0, 255, 80)
    }

    p.draw = () => {
      p.background(255)
      angle = Math.sin(gen * 30) * 30

      p.push()
      p.translate(400, 400)
      p.rotate(gen * 4)
      //here I cam manage amount of curves
      for (var i = 0; i < 100; i++) {
        p.rotate((3 * gen) / 33)
        p.stroke(25, 34, 125)
        p.strokeWeight(1)
        //here i can manage sizes of curves
        p.bezier(i, i, 0, Math.cos(angle - i), 300, angle + i, i + 300, i)
      }
      p.pop()

      p.push()
      p.translate(400, 400)
      p.rotate(gen * 4)
      for (var i = 0; i < 100; i++) {
        p.rotate((3 * gen) / 33)
        p.stroke(0, 0, 0, 90)
        p.fill(255, 255, 255, 20)
        p.strokeWeight(0.1)
        p.bezier(i, -i, 0, angle - i, 400, angle - i, i - 400, i)
      }
      p.pop()

      p.push()
      p.translate(400, 400)
      p.rotate(gen * 8)
      for (var i = 0; i < 12; i++) {
        p.rotate((2 * gen) / 24)
        p.textSize(64)
        p.textFont('Georgia')
        p.fill(0, 0, 0)
        p.noStroke()
        //последними цыфрами управляй расстрянием вжуха
        p.text('kindness', 32, 50)
      }
      p.pop()

      gen += 0.0009
      p.ellipse(300, 300, wind * 12, wind * 12)
    }
  }

  let myp5 = new p5(sketch)
})
