import p5 from 'p5'

function getRandomArbitrary(min, max) {
  return Math.random() * (max - min) + min
}

document.addEventListener('DOMContentLoaded', () => {
  const container = document.getElementsByClassName('prototype_11')[0]
  const frame = document.createElement('div')
  frame.classList.add('frame')
  frame.id = 'frame'
  container.appendChild(frame)

  let sketch = (p) => {
    var angle,
      gen = 120

    p.setup = () => {
      let canvas = p.createCanvas(800, 800)
      canvas.parent('frame')
      p.stroke(200, 0, 255, 80)
      //p.fill(127, 2, 55, 60)
    }

    p.draw = () => {
      p.background(34)
      angle = Math.cos(gen * 10) * 40

      p.push()
      p.translate(400, 400)
      p.rotate(gen * 2)
      for (var i = 0; i < 20; i++) {
        p.rotate((gen * 2) / 45)
        p.fill(
          Math.floor(getRandomArbitrary(0, 256)),
          Math.floor(getRandomArbitrary(120, 180)),
          Math.floor(getRandomArbitrary(0, 56))
        )
        p.strokeWeight(0.86)
        p.bezier(i, i, 0, angle - i, 400, angle - i, i + 200, i)
      }
      p.pop()

      gen += 0.0009
    }
  }

  let myp5 = new p5(sketch)
})
