import p5 from 'p5'

function getRandomArbitrary(min, max) {
  return Math.random() * (max - min) + min
}

//sketch

let sketch = (p) => {
  var angle,
    gen = 333

  p.setup = () => {
    let canvas = p.createCanvas(800, 800)
    canvas.parent('frame')
    p.stroke(200, 0, 255, 80)
    //p.fill(127, 2, 55, 60)
    p.fill(0, 0, 255, 60)
  }

  p.draw = () => {
    p.background(0)
    angle = Math.sin(gen * 30) * 30

    p.push()
    p.translate(400, 400)
    p.rotate(gen * 4)
    //here I cam manage amount of curves
    for (var i = 0; i < 240; i++) {
      p.rotate((3 * gen) / 33)
      p.stroke(25, 34, 125)
      p.strokeWeight(1)
      //here i can manage sizes of curves
      p.bezier(i, i, 0, Math.cos(angle - i), 200, angle + i, i + 200, i)
    }
    p.pop()

    gen += 0.0009
  }
}

document.addEventListener('DOMContentLoaded', () => {
  const container = document.getElementsByClassName('prototype_12')[0]
  const frame = document.createElement('div')
  frame.classList.add('frame')
  frame.id = 'frame'
  container.appendChild(frame)

  //api
  // let apiKey = '5de7c12586e7ea8e8536387ce01bec46'
  // let city = 'Moscow'
  // let url = `https://api.openweathermap.org/data/2.5/onecall?q=${city}&lang=ru&units=metric&appid=${apiKey}`

  let apiKey = '5de7c12586e7ea8e8536387ce01bec46'
  let lat = '55.751244'
  let long = '37.618423'
  let url = `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${long}&exclude=daily,current&appid=${apiKey}`

  fetch(url)
    .then((response) => {
      return response.json()
    })
    .then((data) => {
      for (var i = 0; i < 48; i++) {
        let wind = data.hourly[i].wind_speed
        setTimeout(() => {
          console.log(wind)
        }, 1200 * (i + 1))
      }
    })

  let myp5 = new p5(sketch)
})
